import styled from "@emotion/styled";

const CalendarHeaderGrid = styled.dt`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  padding:1rem 0;
  text-align: center;
  vertical-align:middle;
  button{
    padding:1rem;
    max-width: 40px;
    text-align:center;
    box-sizing: border-box;
  }
  :last-child {
    text-align:right;
  }
`;

const CalendarGrid = styled.li`
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  text-align: center;
  box-sizing: border-box;
  padding: 1rem
`;
function App() {
  let rawData = {
    year: 2021,
    month: 2,
    days: [],
    label: ["일", "월", "화", "수", "목", "금", "토"],
  };
  [...Array(30).keys()].map((i) => {
    rawData.days.push({ day: i + 1, event: [] });
  });

  return (
    <div>
      <header></header>
      <section>
        <dl>
          <CalendarHeaderGrid>
            <button>left</button>
            <span>2021년 02월</span>
            <button>right</button>
          </CalendarHeaderGrid>
          <dd>
            <CalendarGrid>
              {rawData.label.map((i) => {
                return <li key={i}>{i}</li>;
              })}
            </CalendarGrid>
            <CalendarGrid>
              {rawData.days.map((days, index) => {
                return <li key={index}>{days.day}</li>;
              })}
            </CalendarGrid>
          </dd>
        </dl>
      </section>
    </div>
  );
}

export default App;
