import styled from "@emotion/styled";
import dayjs from "dayjs";
import { useEffect, useState } from "react";
const CalendarHeaderGrid = styled.dt`
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  text-align: center;
  vertical-align: middle;
  align-self: stretch;
  padding: 2rem 0;
  font-size: 24px;
  button {
    padding: 0.5rem;
    text-align: center;
    box-sizing: border-box;
    font-weight: 500;
    outline: 0;
  }
  span {
    align-self: center;
    font-weight: bold;
  }
`;

const CalendarGrid = styled.ul`
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  text-align: center;
  font-size: 24px;
`;
const highlightColor = {
  0: "#f0aac9 !important",
  6: "#aac9f0 !important",
};
const CalendarGridItem = styled.li`
  :nth-child(7n + 1) {
    color: #f0aac9 !important;
  }
  :nth-child(7n) {
    color: #aac9f0 !important;
  }
  box-sizing: border-box;
  min-height: 70px;
  color: ${(props) => (props.reserved ? "#cfcfcf" : "#3d3d3d")};

  :hover {
    cursor: pointer;
    position: relative;
    color: ${(props) => (props.reserved ? "#cfcfcf" : "#3d3d3d")};
  }

  :after {
    position: absolute;
    left: calc((100% / 2) - 25px);
    top: 12px;
    max-width: 0px;
    max-height: 20px;
    width: 50px;
    height: 50px;
    content: "";
    box-sizing: border-box;
    background: #aaf0d1;
    opacity: 0;
    z-index: -1;
    display: ${(props) => (props.reserved ? "none" : "block")};
  }

  :hover:after {
    transition: 0.3s all;
    position: absolute;
    left: calc((100% / 2) - 25px);
    top: 12px;
    max-width: 50px;
    max-height: 20px;
    width: 50px;
    height: 50px;
    content: "";
    box-sizing: border-box;
    background: #aaf0d1;
    opacity: 0.5;
    z-index: -1;
    display: ${(props) => (props.reserved ? "none" : "block")};
  }
`;
const Message = styled.span`
  font-size: 12px;
  display: block;
`;

function App() {
  const [now, setNow] = useState(dayjs());

  const defaultData = {
    year: now.year(),
    month: now.month(),
    today: now.date(),
    days: [],
    label: ["일", "월", "화", "수", "목", "금", "토"],
  };
  const [rawData, setRawData] = useState(defaultData);


  useEffect(() => {
    console.log("change", now);
    setRawData(defaultData);
  }, [now]);

  useEffect(() => {

  }, [rawData])

  const changeMonth = (param) => {
        if (param === "prev") {
      // now.subtract(1, "M");
      setNow(now.subtract(1, "M"));
    } else {
      setNow(now.add(1, "M"));

      // now.add(1, "M");
    }

    // const action = {
    //   prev: now.add(-1, "M"),
    //   next: now.add(1, "M"),
    // };
    // action[param];
  };
  return (
    <div>
      <header></header>
      <section>
        <dl>
          <CalendarHeaderGrid>
            <button onClick={() => changeMonth("prev")}>지난달</button>
            <span>2021년 02월</span>
            <button onClick={() => changeMonth("next")}>다음달</button>
          </CalendarHeaderGrid>
          <dd>
            <CalendarGrid>
              {rawData.label.map((day, index) => {
                return (
                  <CalendarGridItem key={index} highlighKey={index}>
                    {day}
                  </CalendarGridItem>
                );
              })}
            </CalendarGrid>
            <CalendarGrid>
              {rawData.days.map((days, index) => {
                return (
                  <CalendarGridItem
                    key={index}
                    reserved={days.event.length > 0}
                  >
                    {days.day} <Message>{days.event[0]}</Message>
                  </CalendarGridItem>
                );
              })}
            </CalendarGrid>
          </dd>
        </dl>
      </section>
    </div>
  );
}

export default App;
