import styled from "@emotion/styled";

const CalendarHeaderGrid = styled.dt`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  text-align: center;
  vertical-align: middle;
  align-self: stretch;

  button {
    padding: 1rem;
    text-align: center;
    box-sizing: border-box;
    font-weight: 500;
  }
  span {
    align-self: center;
    font-weight: bold
  }
`;

const CalendarGrid = styled.ul`
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  text-align: center;
  li {
    box-sizing: border-box;
    padding: 1rem 1rem;
  }
  li:hover {
    cursor: pointer;
    position: relative;
    color: #3d3d3d;
    transition: .5s all;
  }
  li:hover:after {
    position:absolute;
    left:calc((100% - 50px) /2);
    top:0;
    max-width:50px;
    max-height:50px;
    width:50px;
    height:50px;
    content:"";
    box-sizing: border-box;
    background:#99ffcc;
    z-index:-1;
    border-radius:100%;

  }
`;
function App() {
  let rawData = {
    year: 2021,
    month: 2,
    days: [],
    label: ["일", "월", "화", "수", "목", "금", "토"],
  };
  [...Array(30).keys()].map((i) => {
    rawData.days.push({ day: i + 1, event: [] });
  });

  rawData.days[11].event.push("예약완료")

  return (
    <div>
      <header></header>
      <section>
        <dl>
          <CalendarHeaderGrid>
            <button>지난달</button>
            <span>2021년 02월</span>
            <button>다음달</button>
          </CalendarHeaderGrid>
          <dd>
            <CalendarGrid>
              {rawData.label.map((i) => {
                return <li key={i}>{i}</li>;
              })}
            </CalendarGrid>
            <CalendarGrid>
              {rawData.days.map((days, index) => {
                return <li key={index}>{days.day}</li>;
              })}
            </CalendarGrid>
          </dd>
        </dl>
      </section>
    </div>
  );
}

export default App;
